

import java.util.List;

import org.junit.Assert;
import org.junit.BeforeClass;
import org.junit.Test;

import dao.MerchantDao;
import dao.impl.MerchantDaoImpl;
import pojo.Ad;
import pojo.Merchant;


public class MerchantDaoImplTest {

	private static MerchantDao merchantDao;
	@BeforeClass
	public static void init() {
		merchantDao=new MerchantDaoImpl();
	}
	@Test
	public void testGetMerchantById() {
		Merchant m=merchantDao.getMerchantById("1232");
		System.out.println(m.toString());
		Assert.assertTrue(m!=null);
		
	}
	@Test
	public void testUpdateMerchant() {
		Merchant m=new Merchant("123", "2312", "fdafadf", "dffadf", "dafadf", "fdfadsf", "fdafdasf", "fdafa", 2, "fdafa");
		int result=merchantDao.updateMerchant(m);
		Assert.assertTrue(result>0);
	}
	@Test
	public void testAddMerchant() throws Exception {
		Merchant m=new Merchant("1", "2312", "fdafadf", "dffadf", "", "fdfadsf", "fdafdasf", "fdafa", 2, "fdafa");
		int result =merchantDao.addMerchant(m);
		Assert.assertTrue(result>0);
	}
	@Test
	public void testGetAllMerchant() {
		List<Merchant> merchants=merchantDao.getAllMerchant();
		Assert.assertTrue(merchants!=null);
		Assert.assertTrue(merchants.size()>0);
		
	}
	
	@Test
	public void testDeleteMerchant() {
		int result=merchantDao.deleteMerchantByShopId("f34e4835e15a454f889d8fb87b3ab3f8");
		Assert.assertTrue(result>0);
		
	}
	
	@Test
	public void testGetNoCheckMerchantByPage() {
		List<Merchant> merchants=merchantDao.getNoCheckMerchantByPage(0, 3);
		for (Merchant merchant : merchants) {
			System.out.println(merchant.toString());
		}
		
	}
	@Test
	public void findNoCheckMerchantCount() {
		int result=merchantDao.findNoCheckMerchantCount();
		System.out.println(result);
		Assert.assertTrue(result>0);
	}
}
